// packages/dotdo/src/vite/index.ts

import { cloudflare } from '@cloudflare/vite-plugin'
import { tanstackStart } from '@tanstack/react-start/plugin/vite'
import { reactRouter } from '@react-router/dev/vite'

export function dotdo(config: DotdoConfig = {}): Plugin[] {
  const { jsx, framework } = resolveConfig(config)

  const plugins: Plugin[] = []

  // Framework plugin
  if (framework === 'tanstack') {
    plugins.push(tanstackStart())
  } else if (framework === 'react-router') {
    plugins.push(reactRouter())
  }

  // Cloudflare plugin
  plugins.push(cloudflare())

  // Aliasing plugin
  if (jsx === 'hono' || jsx === 'react-compat') {
    plugins.push(honoJsxAliasPlugin())
  }

  return plugins.filter(Boolean)
}
