Make auto-detection tests pass.

## Implementation
```typescript
// packages/dotdo/src/vite/detection.ts

const INCOMPATIBLE_LIBS = [
  'framer-motion',
  'react-three-fiber',
  '@react-three/fiber',
  'react-spring',
]

export function detectJsxRuntime(deps: Record<string, string>): JsxRuntime {
  // Check for incompatible libs first
  if (INCOMPATIBLE_LIBS.some(lib => lib in deps)) return 'react'

  // Check for React deps
  if ('react' in deps) return 'react-compat'

  // Default to hono
  return 'hono'
}

export function detectFramework(deps: Record<string, string>): Framework {
  if ('@tanstack/react-start' in deps) return 'tanstack'
  if ('react-router' in deps) return 'react-router'
  if ('honox' in deps) return 'honox'
  return 'hono'
}
```

