## Implementation
```typescript
interface ActionHandler {
  execute(ticket: Ticket, action: Action, context: ActionContext): Promise<void>
}

class ActionExecutor {
  private handlers: Map<string, ActionHandler> = new Map([
    ['status', new StatusActionHandler()],
    ['priority', new PriorityActionHandler()],
    ['assignee_id', new AssigneeActionHandler()],
    ['group_id', new GroupActionHandler()],
    ['set_tags', new SetTagsActionHandler()],
    ['current_tags', new AddTagsActionHandler()],
    ['remove_tags', new RemoveTagsActionHandler()],
    ['notification_user', new NotificationUserHandler()],
    ['notification_webhook', new WebhookHandler()],
  ])

  async execute(ticket: Ticket, actions: Action[], context: ActionContext) {
    for (const action of actions) {
      const handler = this.handlers.get(action.field)
      if (handler) {
        await handler.execute(ticket, action, context)
      }
    }
  }
}
