## Architecture
```typescript
interface TypedSQL {
  query<T extends object>(
    sql: string,
    params?: unknown[]
  ): Promise<T[]>;

  queryOne<T extends object>(
    sql: string,
    params?: unknown[]
  ): Promise<T | undefined>;

  exec(sql: string, params?: unknown[]): Promise<void>;
}

class TypedSQLiteWrapper implements TypedSQL {
  constructor(private sql: SqlStorage) {}

  async query<T extends object>(
    sql: string,
