export const lakehouseQueryTool: MCPTool = {
  name: 'lakehouse:query',
  description: 'Execute SQL query across hot/warm/cold tiers',
  inputSchema: { ... },

  async execute(input: { sql: string; namespace?: string; explain?: boolean }) {
    const router = new QueryRouter(...)
    const plan = await router.plan(input.sql)

    if (input.explain) {
      return { plan: plan.explain() }
    }

    const results = await router.execute(plan)
    return { rows: results, metadata: plan.metadata }
  }
}
```

