      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${pkg.token}`
      },
      body: JSON.stringify({
        _id: pkg.name,
        name: pkg.name,
        'dist-tags': { latest: pkg.version },
        versions: {
          [pkg.version]: {
            name: pkg.name,
            version: pkg.version,
            dist: {
              tarball: tarball.url,
              shasum: tarball.shasum
            }
          }
        },
        _attachments: {
          [`${pkg.name}-${pkg.version}.tgz`]: {
            content_type: 'application/octet-stream',
            data: tarball.base64
          }
        }
      })
    });

    return { success: response.ok };
