```typescript
export class CachedBuildWorker {
  private cache: KVNamespace;

  async compile(source: string): Promise<BuildResult> {
    const hash = await this.hashSource(source);

    // Check cache
    const cached = await this.cache.get(hash, 'json');
    if (cached) {
      return cached as BuildResult;
    }

    // Build and cache
    const result = await this.buildWorker.compile(source);
    await this.cache.put(hash, JSON.stringify(result), {
      expirationTtl: 86400 // 24 hours
    });

    return result;
  }

  async incrementalBuild(files: Map<string, string>): Promise<BuildResult> {
    const changedFiles = await this.detectChanges(files);
    return this.compileIncremental(changedFiles);
  }
}
